sqlMap.select:
SELECT count(au.album_id) FROM album_udata AS au WHERE au.last_play_time > unix_timestamp(#passDate#)
au.last_play_time < #lastplaytime#
LIMIT 1;

SELECT a.artist_id, a.artist_name, a.artist_region, a.artist_type, a.artist_thumbnail, 0 AS published_albums FROM artist as a WHERE a.artist_id IN
#artistIdList[]#
AND (a.status=0 or a.status=1 or a.status=6);

select o.uid from (select a.uid,FLOOR(RAND()*500) ra from user_random_id a where a.uid not in
#uidList[]#
) o order by o.ra limit #pagecount#;
